var link=document.querySelector(".feedback-link"),overlay=document.querySelector(".overlay"),popup=document.querySelector(".feedback"),close=popup.querySelector(".modal-close"),form=popup.querySelector("form"),userName=popup.querySelector("[name=user-name]"),userEmail=popup.querySelector("[name=user-email]"),userComment=popup.querySelector("[name=comment]");link.addEventListener("click",function(e){e.preventDefault(),overlay.classList.add("modal-show")}),close.addEventListener("click",function(e){e.preventDefault(),overlay.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){userName.value&&userEmail.value&&userComment.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&overlay.classList.contains("modal-show")&&(e.preventDefault(),overlay.classList.remove("modal-show"),popup.classList.remove("modal-error"))});
